def _print(level: str, text: str) -> None:
    print(f"{level.upper()}: {text}")


def info(text: str) -> None:
    _print("info", text)


def error(text: str) -> None:
    _print("error", text)


def success(text: str) -> None:
    _print("success", text)


def warning(text: str) -> None:
    _print("warning", text)
